<style>
	@media (min-width: 20rem) {
		section {
			background-color: var(--light-primary);
			bottom: 0;
			display: block;
			left: 0;
			position: fixed;
			width: 100%;
			z-index: 100;
		}

		nav {
			background-color: var(--light-primary);
			margin: 0 auto;
			padding: 0.6rem 0;
			padding: 1rem;
			width: 100%;
			bottom: 0;
			position: fixed;
            overflow: auto;
		}

		ul {
			align-items: center;
			display: flex;
			flex-direction: row;
			gap: 7.5vw;
			justify-content: space-between;
			width: fit-content;
			margin: auto;
		}

		li {
			list-style-type: none;
		}

        li:last-child {
            margin-right: 1rem;
        }

		figure {
			align-items: center;
			display: flex;
			flex-direction: column;
			row-gap: 0.2rem;
		}

		figcaption {
			color: var(--light-subheading-color);
			font-family: 'Inter SemiBold';
			font-size: 0.75rem;
		}

		a {
			align-items: center;
			color: var(--color-default);
			display: flex;
			gap: 0.5rem;
			padding: 0.5rem;
			position: relative;
			text-decoration: none;
			transition: 0.3s ease-in-out;
		}

		a svg path {
			transition: 0.2s cubic-bezier(0.075, 0.82, 0.165, 1);
		}

		a:is(:hover, :focus) figcaption {
			color: #5a00d8;
			font-weight: 600;
			transform: rotate(-8deg) translateY(0rem);
		}

		a:is(:hover, :focus) svg {
			transform: rotate(24deg) translateY(-0.33rem);
		}

		a:is(:hover, :focus) svg path {
			stroke: #b586f8;
			stroke-width: 2.5px;
		}

		a:active {
			color: #5a00d8;
			font-weight: 700;
		}

		a:active svg path {
			stroke: #b586f8;
			stroke-width: 2.5px;
		}
	}

	@media (min-width: 64rem) {
		section {
			display: none;
		}
	}
</style>
